mapper.select:
    	SELECT acc.id,su.nick_name AS nickName,su.logo_path AS logoPath,acc.content,acc.uid,
		CAST(acc.create_time AS CHAR ) AS createTime 
		FROM app_content_comment AS acc 
		INNER JOIN sys_user AS su ON acc.uid = su.id 
		WHERE acc.content_id = #{id} 
		ORDER BY acc.id DESC LIMIT #{page},#{rows}

SELECT cc.id,c.item_name AS itemName,(CASE cc.record_type WHEN 1 THEN '公告' ELSE '动态' END) AS recordType, cc.content,cc.likes,(SELECT COUNT(1) FROM app_content_comment WHERE content_id=cc.id) AS comments, CAST(cc.create_time AS CHAR) AS createTime,CONCAT('/dn',s.value) AS iconValue,u.user_name AS userName,u.logo_path AS logoPath, u.nick_name AS nickName,(CASE cc.is_report WHEN 1 THEN '是' ELSE '否' END) AS isReport FROM app_community_content cc INNER JOIN app_community c ON c.id=cc.community_id INNER JOIN sys_user u ON u.id=cc.uid LEFT JOIN sys_setting s ON s.id=cc.icon_value
and c.item_name like '%${itemName}%'
and u.nick_name like '%${nickName}%'
and cc.record_type = #{param.recordType}
and cc.is_report = #{param.isReport}
ORDER BY cc.create_time DESC LIMIT #{page},#{rows}

SELECT COUNT(1) FROM app_community_content cc INNER JOIN app_community c ON c.id=cc.community_id INNER JOIN sys_user u ON u.id=cc.uid
and c.item_name like '%${itemName}%'
and u.nick_name like '%${nickName}%'
and cc.record_type = #{param.recordType}
and cc.is_report = #{param.isReport}

mapper.sql:

select a.* from app_community_content a


        and a.id = #{param.id,jdbcType=BIGINT}


        and a.community_id = #{param.communityId,jdbcType=BIGINT}


        and a.record_type = #{param.recordType,jdbcType=INTEGER}


        and a.logo_path = #{param.logoPath,jdbcType=VARCHAR}


        and a.logo_path2 = #{param.logoPath2,jdbcType=VARCHAR}


        and a.logo_path3 = #{param.logoPath3,jdbcType=VARCHAR}


        and a.icon_value = #{param.iconValue,jdbcType=VARCHAR}


        and a.likes = #{param.likes,jdbcType=INTEGER}


        and a.is_report = #{param.isReport,jdbcType=INTEGER}


        and a.uid = #{param.uid,jdbcType=BIGINT}


        and a.content = #{param.content,jdbcType=LONGVARCHAR}


order by a.id desc

