mapper.sql:

select a.* from app_course_system a


        and a.id = #{param.id,jdbcType=BIGINT}


        and a.system_id = #{param.systemId,jdbcType=BIGINT}


        and a.course_id = #{param.courseId,jdbcType=BIGINT}


        and a.record_type = #{param.recordType,jdbcType=INTEGER}


        and a.item_remark = #{param.itemRemark,jdbcType=VARCHAR}


order by a.id desc

