mapper.sql:

select a.* from app_course_teacher a


        and a.id = #{param.id,jdbcType=BIGINT}


        and a.course_id = #{param.courseId,jdbcType=BIGINT}


        and a.teacher_id = #{param.teacherId,jdbcType=BIGINT}


        and a.logo_path = #{param.logoPath,jdbcType=VARCHAR}


        and a.user_name = #{param.userName,jdbcType=VARCHAR}


        and a.remark = #{param.remark,jdbcType=VARCHAR}


        and a.salary = #{param.salary,jdbcType=DECIMAL}


        and a.teach_times = #{param.teachTimes,jdbcType=INTEGER}


        and a.rate = #{param.rate,jdbcType=DECIMAL}


order by a.id desc

