mapper.sql:

select a.* from app_course_type a


        and a.id = #{param.id,jdbcType=BIGINT}


        and a.item_name = #{param.itemName,jdbcType=VARCHAR}


        and a.course_tag = #{param.courseTag,jdbcType=VARCHAR}


        and a.course_tag_type = #{param.courseTagType,jdbcType=VARCHAR}


        and a.course_group = #{param.courseGroup,jdbcType=VARCHAR}


        and a.course_group_type = #{param.courseGroupType,jdbcType=VARCHAR}


        and a.blue_currency_rate = #{param.blueCurrencyRate,jdbcType=DECIMAL}


        and a.retrain_amount = #{param.retrainAmount,jdbcType=DECIMAL}


        and a.course_month = #{param.courseMonth,jdbcType=VARCHAR}


        and a.course_month_type = #{param.courseMonthType,jdbcType=VARCHAR}


order by a.id desc

