mapper.sql:

select a.* from app_course_use a


        and a.id = #{param.id,jdbcType=BIGINT}


        and a.uid = #{param.uid,jdbcType=BIGINT}


        and a.use_month = #{param.useMonth,jdbcType=VARCHAR}


        and a.unuse_course_name = #{param.unuseCourseName,jdbcType=VARCHAR}


        and a.use_course_name = #{param.useCourseName,jdbcType=VARCHAR}


        and a.use_counts = #{param.useCounts,jdbcType=INTEGER}


        and a.use_amount = #{param.useAmount,jdbcType=DECIMAL}


        and a.unuse_counts = #{param.unuseCounts,jdbcType=INTEGER}


        and a.unuse_amount = #{param.unuseAmount,jdbcType=DECIMAL}


order by a.id desc

