mapper.select:
SELECT t.item_name AS itemName,t.id,t.logo_path AS logoPath,t.item_remark AS itemRemark, t.teacher_level AS teacherLevel,t.item_theme AS itemTheme FROM app_teacher t
AND t.record_type = 1
AND t.id IN (SELECT DISTINCT teacher_id FROM app_teacher_district WHERE tag_value = #{district})
AND t.teacher_level = #{level}
AND t.item_name LIKE '%${itemName}%'
ORDER BY t.sort DESC LIMIT #{page},#{rows}

    	SELECT t.id,t.logo_path AS logoPath,t.item_name AS itemName,t.item_content AS itemContent,t.item_remark AS itemRemark,t.is_home as isHome,t.sort,
		t.item_theme AS itemTheme,t.teacher_level AS teacherLevel,t.tag_type AS tagType,t.tag_value AS tagValue,CAST(t.create_time AS CHAR) AS createTime,
		(SELECT GROUP_CONCAT(td.tag_value) FROM app_teacher_district td WHERE td.teacher_id=t.id) AS district,u.real_name as realName,
		(SELECT COUNT(1) FROM app_course_online co WHERE co.teacher_id=t.id) AS onlines,u.user_name AS userName,u.logo_path as logoPath1 
		FROM app_teacher t
		LEFT JOIN sys_user u ON t.uid=u.id

			and t.record_type = #{param.recordType}

				and EXISTS(SELECT d.teacher_id FROM app_teacher_district d WHERE d.teacher_id=t.id AND d.tag_value=#{district})


				and t.teacher_level = #{param.teacherLevel}


				and t.item_name like '%${param.itemName}%'


				and t.tag_value = #{param.tagValue}


		ORDER BY t.create_time DESC LIMIT #{page},#{rows}

    	SELECT COUNT(1)
		FROM app_teacher t

			and t.record_type = #{param.recordType}

				and EXISTS(SELECT d.teacher_id FROM app_teacher_district d WHERE d.teacher_id=t.id AND d.tag_value=#{district})


				and t.teacher_level = #{param.teacherLevel}


				and t.item_name like '%${param.itemName}%'

mapper.sql:

select a.* from app_teacher a


        and a.id = #{param.id,jdbcType=BIGINT}


        and a.logo_path = #{param.logoPath,jdbcType=VARCHAR}


        and a.uid = #{param.uid,jdbcType=BIGINT}


        and a.teacher_level = #{param.teacherLevel,jdbcType=VARCHAR}


        and a.item_name = #{param.itemName,jdbcType=VARCHAR}


        and a.item_remark = #{param.itemRemark,jdbcType=VARCHAR}


        and a.item_theme = #{param.itemTheme,jdbcType=VARCHAR}


        and a.record_type = #{param.recordType,jdbcType=INTEGER}


        and a.tag_value = #{param.tagValue,jdbcType=VARCHAR}


        and a.tag_type = #{param.tagType,jdbcType=VARCHAR}


        and a.sort = #{param.sort,jdbcType=INTEGER}


        and a.is_home = #{param.isHome,jdbcType=INTEGER}


        and a.item_content = #{param.itemContent,jdbcType=LONGVARCHAR}


order by a.id desc

