mapper.select:
    	SELECT b.id,b.phone,b.actual_name AS actualName,b.borrow_amount AS borrowAmount,b.use_remark AS useRemark,
		b.start_date AS startDate,b.end_date AS endDate,b.borrow_year AS borrowYear,b.year_rate AS yearRate,b.year_interest AS yearInterest,
		b.return_remark AS returnRemark,b.balance_amount AS balanceAmount,SUBSTRING(CAST(b.create_time AS CHAR),1,16) AS createTime
		FROM app_report_borrow b


				b.actual_name LIKE '%${realName}%'


				AND b.create_time >= #{startTime} AND b.create_time <= #{endTime}


				AND b.create_time >= #{startTime}


				AND b.create_time <= #{endTime}


		ORDER BY b.create_time DESC LIMIT #{page},#{rows}

    	SELECT COUNT(1)
		FROM app_report_borrow b


				b.actual_name LIKE '%${realName}%'


				AND b.create_time >= #{startTime} AND b.create_time <= #{endTime}


				AND b.create_time >= #{startTime}


				AND b.create_time <= #{endTime}

mapper.sql:

select a.* from app_report_borrow a


        and a.id = #{param.id,jdbcType=BIGINT}


        and a.phone = #{param.phone,jdbcType=VARCHAR}


        and a.actual_name = #{param.actualName,jdbcType=VARCHAR}


        and a.borrow_amount = #{param.borrowAmount,jdbcType=DECIMAL}


        and a.use_remark = #{param.useRemark,jdbcType=VARCHAR}


        and a.start_date = #{param.startDate,jdbcType=VARCHAR}


        and a.end_date = #{param.endDate,jdbcType=VARCHAR}


        and a.borrow_year = #{param.borrowYear,jdbcType=VARCHAR}


        and a.year_rate = #{param.yearRate,jdbcType=DECIMAL}


        and a.year_interest = #{param.yearInterest,jdbcType=DECIMAL}


        and a.return_remark = #{param.returnRemark,jdbcType=VARCHAR}


        and a.balance_amount = #{param.balanceAmount,jdbcType=DECIMAL}


order by a.id desc

