mapper.select:
SELECT cs.id,cs.tag_value AS tagValue,CAST(cs.create_time AS CHAR) AS createTime,c.item_name AS itemName, su.user_name AS managerPhone,su.real_name AS managerName,u.user_name AS userName,u.real_name AS realName, u.logo_path AS logoPath,cs.uid FROM app_community_staff cs INNER JOIN app_community c ON c.id=cs.community_id INNER JOIN sys_user su ON su.id=c.uid INNER JOIN sys_user u ON u.id=cs.uid
and cs.tag_value = #{tagValue}
and c.item_name like '%${itemName}%'
and su.user_name like '%${managerPhone}%'
and u.real_name like '%${realName}%'
ORDER BY cs.create_time DESC LIMIT #{page},#{rows}

mapper.sql:

select a.* from app_community_staff a


        and a.id = #{param.id,jdbcType=BIGINT}


        and a.community_id = #{param.communityId,jdbcType=BIGINT}


        and a.tag_value = #{param.tagValue,jdbcType=VARCHAR}


        and a.tag_type = #{param.tagType,jdbcType=VARCHAR}


        and a.uid = #{param.uid,jdbcType=BIGINT}


order by a.id desc

