mapper.select:
    	SELECT c.id,c.item_name AS itemName,c.logo_path AS logoPath,c.amount,c.is_public AS isPublic,co.course_content as courseContent,
		c.record_type AS recordType,c.state,CAST(c.create_time AS CHAR) AS createTime,ct.item_name AS courseTypeId,co.subsidy,
		co.register_date AS registerDate,co.is_recommend AS isRecommend,co.offline_amount AS offlineAmount,co.remark,
		CONCAT(co.province,co.city,co.district,co.address) AS pcda,co.course_remark as courseRemark,c.sort,
		(SELECT GROUP_CONCAT(t.user_name) FROM app_course_teacher t WHERE t.course_id = c.id) AS teacher,
		(SELECT GROUP_CONCAT(cc.user_name) FROM app_course_contact cc WHERE cc.course_id = c.id) AS contact
		FROM app_course c 
		INNER JOIN app_course_offline co ON c.id=co.id
		INNER JOIN app_course_type ct ON co.course_type_id=ct.id

			c.record_type=1 and c.state != 0

				and c.state = #{state}


				and c.item_name like '%${itemName}%'


				and c.amount >= #{amount}


				and co.is_recommend = #{isRecommend}


				and co.course_type_id = #{courseTypeId}


		ORDER BY c.create_time DESC LIMIT #{page},#{rows}

SELECT c.id,c.item_name AS itemName,c.logo_path AS logoPath,c.amount,c.is_public AS isPublic, c.record_type AS recordType, (case c.state when 1 then '报名中' when 2 then '报名结束' when 3 then '已开课' else '' end) as state, CAST(c.create_time AS CHAR) AS createTime,ct.item_name AS courseTypeId,co.subsidy, co.register_date AS registerDate, (case co.is_recommend when 0 then '否' when 1 then '是' else '' end) AS isRecommend, co.offline_amount AS offlineAmount,co.remark, CONCAT(co.province,co.city,co.district,co.address) AS pcda,co.course_remark as courseRemark,c.sort, (SELECT GROUP_CONCAT(t.user_name) FROM app_course_teacher t WHERE t.course_id = c.id) AS teacher, (SELECT GROUP_CONCAT(cc.user_name) FROM app_course_contact cc WHERE cc.course_id = c.id) AS contact FROM app_course c INNER JOIN app_course_offline co ON c.id=co.id INNER JOIN app_course_type ct ON co.course_type_id=ct.id
c.record_type=1 and c.state != 0
and c.state = #{state}
and c.item_name like '%${itemName}%'
and c.amount >= #{amount}
and co.is_recommend = #{isRecommend}
and co.course_type_id = #{courseTypeId}
ORDER BY c.create_time DESC

    	SELECT COUNT(1)
		FROM app_course c 
		INNER JOIN app_course_offline co ON c.id=co.id
		INNER JOIN app_course_type ct ON co.course_type_id=ct.id

			c.record_type=1 and c.state != 0

				and c.state = #{state}


				and c.item_name like '%${itemName}%'


				and c.amount >= #{amount}


				and co.is_recommend = #{isRecommend}


				and co.course_type_id = #{courseTypeId}

mapper.sql:

select a.* from app_course_offline a


        and a.id = #{param.id,jdbcType=BIGINT}


        and a.course_type_id = #{param.courseTypeId,jdbcType=BIGINT}


        and a.register_date = #{param.registerDate,jdbcType=VARCHAR}


        and a.province = #{param.province,jdbcType=VARCHAR}


        and a.city = #{param.city,jdbcType=VARCHAR}


        and a.district = #{param.district,jdbcType=VARCHAR}


        and a.address = #{param.address,jdbcType=VARCHAR}


        and a.offline_amount = #{param.offlineAmount,jdbcType=DECIMAL}


        and a.is_recommend = #{param.isRecommend,jdbcType=INTEGER}


        and a.course_id = #{param.courseId,jdbcType=BIGINT}


        and a.course_id2 = #{param.courseId2,jdbcType=BIGINT}


        and a.phone = #{param.phone,jdbcType=VARCHAR}


        and a.subsidy = #{param.subsidy,jdbcType=DECIMAL}


        and a.remark = #{param.remark,jdbcType=VARCHAR}


        and a.course_content = #{param.courseContent,jdbcType=LONGVARCHAR}


        and a.course_remark = #{param.courseRemark,jdbcType=LONGVARCHAR}


order by a.id desc

