mapper.select:
    	SELECT co.item_remark AS itemRemark,co.course_content AS courseContent,co.course_group AS courseGroup,c.is_financial as isFinancial,
		co.course_type AS courseType,co.id,co.rate,co.vedio_link AS vedioLink,co.vedio_palys AS vedioPalys,c.logo_path as logoPath,
		co.teacher_name AS teacherName,c.item_name AS itemName,c.amount,CAST(c.create_time as CHAR) as createTime,c.sort,
		(SELECT GROUP_CONCAT(ct.tag_identity) FROM app_course_tag ct WHERE ct.course_id=c.id) AS ct,co.link_type as linkType
		FROM app_course_online co
		INNER JOIN app_course c ON c.id=co.id

			c.record_type=2 and c.state != 0

				and c.item_name like '%${itemName}%'


				and c.amount >= #{amount}


				and co.course_group = #{courseGroup}


				and co.teacher_name like '%${teacherName}%'


				and co.teacher_id = #{teacherId}


		ORDER BY c.create_time DESC LIMIT #{page},#{rows}

    	SELECT COUNT(1)
		FROM app_course_online co
		INNER JOIN app_course c ON c.id=co.id

			c.record_type=2 and c.state != 0

				and c.item_name like '%${itemName}%'


				and c.amount >= #{amount}


				and co.course_group = #{courseGroup}


				and co.teacher_name like '%${teacherName}%'


				and co.teacher_id = #{teacherId}

mapper.sql:

select a.* from app_course_online a


        and a.id = #{param.id,jdbcType=BIGINT}


        and a.item_remark = #{param.itemRemark,jdbcType=VARCHAR}


        and a.course_group = #{param.courseGroup,jdbcType=VARCHAR}


        and a.course_type = #{param.courseType,jdbcType=INTEGER}


        and a.vedio_link = #{param.vedioLink,jdbcType=VARCHAR}


        and a.vedio_palys = #{param.vedioPalys,jdbcType=INTEGER}


        and a.teacher_id = #{param.teacherId,jdbcType=BIGINT}


        and a.teacher_logo_path = #{param.teacherLogoPath,jdbcType=VARCHAR}


        and a.teacher_name = #{param.teacherName,jdbcType=VARCHAR}


        and a.teacher_remark = #{param.teacherRemark,jdbcType=VARCHAR}


        and a.rate = #{param.rate,jdbcType=DECIMAL}


        and a.course_content = #{param.courseContent,jdbcType=LONGVARCHAR}


        and a.link_type = #{param.linkType,jdbcType=INTEGER}


order by a.id desc

