mapper.select:
    	SELECT * FROM user_coupon c
		WHERE c.uid=#{uid} AND c.course_id=(SELECT course_type_id FROM app_course_offline WHERE id=#{courseId})
		AND c.date_from <=#{startTime}

    	SELECT c.coupon_name AS couponName,c.coupon_content AS couponContent,CAST(c.create_time AS CHAR) AS createTime,
		CONCAT(CAST(c.date_from AS CHAR),'-',CAST(c.date_to AS CHAR)) AS useDate
		FROM user_coupon c
		WHERE c.uid = #{param.uid} AND c.state = #{param.state}
		ORDER BY c.create_time DESC LIMIT #{page},#{rows}

    	SELECT COUNT(1)
		FROM user_coupon uc 
		where uc.uid=#{param.uid}

mapper.sql:

select a.* from user_coupon a


        and a.id = #{param.id,jdbcType=BIGINT}


        and a.uid = #{param.uid,jdbcType=BIGINT}


        and a.course_id = #{param.courseId,jdbcType=BIGINT}


        and a.coupon_name = #{param.couponName,jdbcType=VARCHAR}


        and a.coupon_content = #{param.couponContent,jdbcType=VARCHAR}


        and a.date_from = #{param.dateFrom,jdbcType=DATE}


        and a.date_to = #{param.dateTo,jdbcType=DATE}


        and a.state = #{param.state,jdbcType=INTEGER}


order by a.id desc

