mapper.delete:

delete from user_coupon


        and id = #{param.id,jdbcType=BIGINT}


        and uid = #{param.uid,jdbcType=BIGINT}


        and course_id = #{param.courseId,jdbcType=BIGINT}


        and coupon_name = #{param.couponName,jdbcType=VARCHAR}


        and coupon_content = #{param.couponContent,jdbcType=VARCHAR}


        and date_from = #{param.dateFrom,jdbcType=DATE}


        and date_to = #{param.dateTo,jdbcType=DATE}


        and state = #{param.state,jdbcType=INTEGER}
