mapper.select:
SELECT p.id,p.logo_path AS logoPath,p.item_remark AS itemRemark,p.price,p.item_name AS itemName, (SELECT COUNT(1) FROM app_product_order WHERE product_id=p.id AND state=3) AS counts FROM app_product p
and p.item_name like '%${itemName}%'
and p.category_id = #{categoryId}
ORDER BY counts ASC,counts DESC, price ASC,price DESC, p.row_seq DESC,p.create_time DESC LIMIT #{page},#{rows}

mapper.sql:

select a.* from app_product a


        and a.id = #{param.id,jdbcType=BIGINT}


        and a.logo_path = #{param.logoPath,jdbcType=VARCHAR}


        and a.category_id = #{param.categoryId,jdbcType=BIGINT}


        and a.item_name = #{param.itemName,jdbcType=VARCHAR}


        and a.item_remark = #{param.itemRemark,jdbcType=VARCHAR}


        and a.row_seq = #{param.rowSeq,jdbcType=INTEGER}


        and a.price = #{param.price,jdbcType=DECIMAL}


        and a.item_comment = #{param.itemComment,jdbcType=LONGVARCHAR}


order by a.id desc

