mapper.delete:

delete from app_product


        and id = #{param.id,jdbcType=BIGINT}


        and logo_path = #{param.logoPath,jdbcType=VARCHAR}


        and category_id = #{param.categoryId,jdbcType=BIGINT}


        and item_name = #{param.itemName,jdbcType=VARCHAR}


        and item_remark = #{param.itemRemark,jdbcType=VARCHAR}


        and row_seq = #{param.rowSeq,jdbcType=INTEGER}


        and price = #{param.price,jdbcType=DECIMAL}


        and item_comment = #{param.itemComment,jdbcType=LONGVARCHAR}

mapper.select:
SELECT p.id, p.`item_name`, p.`item_remark`, p.`item_comment`, p.`logo_path`, p.`row_seq`, p.`price`, DATE_FORMAT(p.`create_time`,'%Y-%m-%d %H:%i:%S') AS create_time, (SELECT SUM(api.`stock_qty`) FROM app_product_inventory api WHERE api.`product_id` = p.`id`)AS totalStockQty, (SELECT pc.item_name FROM app_product_category AS pc WHERE pc.id = p.`category_id`)AS category_name, (SELECT pc.id FROM app_product_category AS pc WHERE pc.id = p.`category_id`)AS categoryId FROM app_product p
p.item_name like CONCAT('%',#{param.itemName,jdbcType=VARCHAR},'%')
and p.id = #{param.id,jdbcType=BIGINT}
ORDER BY p.`create_time` DESC ,p.`row_seq` ASC
limit #{page,jdbcType=INTEGER},
#{rows,jdbcType=INTEGER}
