SELECT a.id,a.remark,a.logo_path as logoPath,CAST(a.create_time AS CHAR) AS createTime,u.user_name AS userName, u.real_name AS realName,t.item_name as itemName,(SELECT COUNT(1) FROM app_accompany_like al WHERE al.accompany_id=a.id) AS likes FROM app_accompany a INNER JOIN app_teacher t ON t.id=a.teacher_id INNER JOIN sys_user u ON u.id=t.uid
a.teacher_id = #{teacherId}
ORDER BY a.create_time DESC LIMIT #{page},#{rows}

