mapper.sql:

select a.* from app_accompany a


        and a.id = #{param.id,jdbcType=BIGINT}


        and a.teacher_id = #{param.teacherId,jdbcType=BIGINT}


        and a.remark = #{param.remark,jdbcType=VARCHAR}


        and a.logo_path = #{param.logoPath,jdbcType=VARCHAR}


        and a.logo_path2 = #{param.logoPath2,jdbcType=VARCHAR}


        and a.logo_path3 = #{param.logoPath3,jdbcType=VARCHAR}


order by a.id desc

