mapper.select:
 select temp.*
 from (
 select 

	,getDistance(longitude,latitude,#{param.longitude,jdbcType=DECIMAL},#{param.latitude,jdbcType=DECIMAL}) as distance
	from app_company_hall where company_id = #{param.companyId,jdbcType=VARCHAR}
) temp order by temp.distance ASC

SELECT temp.*,su.user_name,ac.company_name, (SELECT SUM(amount) FROM user_order uo LEFT JOIN user_number un ON uo.unid = un.id LEFT JOIN app_community ac ON un.community_id = ac.id WHERE ac.company_hall_id = temp.id AND uo.is_pay = 1 AND DATE_FORMAT(uo.create_time,'%Y%m') = DATE_FORMAT(NOW(),'%Y%m') ) AS monthMoney, (SELECT SUM(amount) FROM user_order uo LEFT JOIN user_number un ON uo.unid = un.id LEFT JOIN app_community ac ON un.community_id = ac.id WHERE ac.company_hall_id = temp.id AND uo.is_pay = 1 ) AS totalMoney FROM app_company_hall temp LEFT JOIN sys_system_user su ON temp.update_by = su.id LEFT JOIN app_company ac ON temp.company_id = ac.id
and temp.id = #{param.id,jdbcType=BIGINT}
and temp.company_id = #{param.companyId,jdbcType=BIGINT}
and temp.hall_name like CONCAT('%',#{param.hallName,jdbcType=VARCHAR},'%')
